CREATE DEFINER=`root`@`localhost` PROCEDURE `CONFIRMAR_LOTE_MEDICAMENTO`()
BEGIN
 DECLARE V_COD_MEDICAMENTO INTEGER;
 DECLARE V_CANT_MEDICAMENTO INTEGER;
  DECLARE V_STATUS_DETALLE VARCHAR(30);
 DECLARE V_FINAL INTEGER DEFAULT 0;

  DECLARE MEDICAMENTO_CUR CURSOR FOR SELECT D.COD_MEDICAMENTO,D.CAN_MEDICAMENTO,D.STATUS_DETALLE 
  FROM TBL_PR_DETALLE_LOTE D JOIN TBL_PR_COMPRA_MEDICAMENTO C ON(C.COD_COMPRA_MEDICAMENTO = D.COD_COMPRA_MEDICAMENTO)
  WHERE  D.COD_COMPRA_MEDICAMENTO =  ( SELECT MAX(COD_COMPRA_MEDICAMENTO) FROM TBL_PR_COMPRA_MEDICAMENTO);
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET  V_FINAL = 1;
  
START TRANSACTION;



  OPEN MEDICAMENTO_CUR;

  bucle: LOOP

    FETCH MEDICAMENTO_CUR INTO V_COD_MEDICAMENTO,V_CANT_MEDICAMENTO,V_STATUS_DETALLE;

    IF V_FINAL = 1 THEN
      LEAVE bucle;
    END IF;
    
    IF  V_STATUS_DETALLE = 'EN PROCESO'  THEN

    UPDATE TBL_PR_MEDICAMENTO SET CAN_DISPONIBLE = CAN_DISPONIBLE + V_CANT_MEDICAMENTO 
    WHERE COD_MEDICAMENTO = V_COD_MEDICAMENTO;
    END IF;
  END LOOP bucle;
 
  CLOSE MEDICAMENTO_CUR;
   SELECT @COD_COMPRA_MEDICAMENTO:=MAX( COD_COMPRA_MEDICAMENTO) FROM TBL_PR_COMPRA_MEDICAMENTO;
SELECT @COD_MEDICAMENTO:= COD_MEDICAMENTO FROM TBL_PR_DETALLE_LOTE WHERE COD_COMPRA_MEDICAMENTO = @COD_COMPRA_MEDICAMENTO;

UPDATE TBL_PR_COMPRA_MEDICAMENTO 
SET STATUS_LOTE = 'CONFIRMADO'
WHERE COD_COMPRA_MEDICAMENTO = @COD_COMPRA_MEDICAMENTO;

UPDATE TBL_PR_DETALLE_LOTE
SET STATUS_DETALLE = 'CONFIRMADA'
WHERE COD_COMPRA_MEDICAMENTO = @COD_COMPRA_MEDICAMENTO;

COMMIT;
END ;;
