CREATE DEFINER=`root`@`localhost` PROCEDURE `DETALLE_LOTE`(IN V_COD_COMPRA_GANADO BIGINT)
BEGIN
START TRANSACTION; select
`d`.`COD_DETALLE_COMPRA` AS `COD_DETALLE_COMPRA`,
`c`.`COD_COMPRA_GANADO` AS `COD_COMPRA_GANADO`,
`d`.`NOM_GANADO` AS `NOM_GANADO`,
`d`.`CLR_GANADO` AS `CLR_GANADO`,
`r`.`NOM_RAZA` AS `NOM_RAZA`,
`d`.`SEX_GANADO` AS `SEX_GANADO`,
`d`.`PES_ACTUAL` AS `PES_ACTUAL`,
`e`.`DET_ESTADO` AS `DET_ESTADO`,
`d`.`FIE_GANADO` AS `FIE_GANADO`,
`l`.`DIR_LUGAR` AS `DIR_LUGAR`,
`d`.`PRE_GANADO` AS `PRE_GANADO`,
`d`.`STATUS_COMPRA` AS `STATUS` 
from ((((`tbl_mc_compra_ganado` `c`
join `tbl_mc_detalle_comprag` `d` on(`c`.`COD_COMPRA_GANADO` = `d`.`COD_COMPRA_GANADO`))
join `tbl_mg_raza` `r` on(`d`.`COD_RAZA` = `r`.`COD_RAZA`))
join `tbl_mg_estado_ganado` `e` on(`d`.`COD_ESTADO` = `e`.`COD_ESTADO`)) 
join `tbl_mg_lugar` `l` on(`d`.`COD_LUGAR` = `l`.`COD_LUGAR`)) 
where c.COD_COMPRA_GANADO = V_COD_COMPRA_GANADO; 
COMMIT;
END ;;
