CREATE DEFINER=`root`@`localhost` PROCEDURE `INS_COMPRA_MEDICAMENTO`(IN V_COD_MEDICAMENTO BIGINT,
IN V_CAN_MEDICAMENTO INT)
BEGIN
  START TRANSACTION;
   SELECT @COD_COMPRA_MEDICAMENTO :=MAX(COD_COMPRA_MEDICAMENTO) FROM `TBL_PR_COMPRA_MEDICAMENTO`;
   SELECT @DETALLE := COD_DETALLE_LOTE FROM TBL_PR_DETALLE_LOTE WHERE COD_MEDICAMENTO = V_COD_MEDICAMENTO; 
   IF EXISTS ( SELECT COD_MEDICAMENTO FROM TBL_PR_DETALLE_LOTE WHERE COD_MEDICAMENTO = V_COD_MEDICAMENTO AND COD_COMPRA_MEDICAMENTO = @COD_COMPRA_MEDICAMENTO AND COD_DETALLE_LOTE = @DETALLE) THEN
   UPDATE TBL_PR_DETALLE_LOTE SET
   CAN_MEDICAMENTO = CAN_MEDICAMENTO + V_CAN_MEDICAMENTO,
   UNI_DISPONIBLES = UNI_DISPONIBLES + V_CAN_MEDICAMENTO
   WHERE  COD_COMPRA_MEDICAMENTO = @COD_COMPRA_MEDICAMENTO AND COD_DETALLE_LOTE = @DETALLE;
   ELSE
  INSERT INTO TBL_PR_DETALLE_LOTE(            
  COD_MEDICAMENTO,   
  CAN_MEDICAMENTO,
  COD_COMPRA_MEDICAMENTO,
  STATUS_DETALLE,
  UNI_DISPONIBLES
  )
  VALUES( 
  V_COD_MEDICAMENTO,   
  V_CAN_MEDICAMENTO,
  @COD_COMPRA_MEDICAMENTO,
  DEFAULT,
  V_CAN_MEDICAMENTO
  );
  UPDATE TBL_PR_COMPRA_MEDICAMENTO set CAN_TOTAL= CAN_TOTAL + 1
where COD_COMPRA_MEDICAMENTO = @COD_COMPRA_MEDICAMENTO;
END IF;
  
  COMMIT;
  
END ;;
