CREATE DEFINER=`root`@`localhost` PROCEDURE `INS_ORDENT`(V_COD_MEDICAMENTO BIGINT, V_CAN_MEDICAMENTO INT)
BEGIN
   DECLARE V_LOTE INTEGER;
 DECLARE V_DISPONIBLE INTEGER;
  DECLARE V_REQUERIDAS INTEGER;
 DECLARE V_VENCIMIENTO DATE;


  DECLARE LOTE_CUR CURSOR FOR SELECT C.COD_COMPRA_MEDICAMENTO,D.UNI_DISPONIBLES
  FROM TBL_PR_DETALLE_LOTE D JOIN TBL_PR_COMPRA_MEDICAMENTO C ON(D.COD_COMPRA_MEDICAMENTO = C.COD_COMPRA_MEDICAMENTO)
  WHERE D.COD_MEDICAMENTO = V_COD_MEDICAMENTO
  ORDER BY C.FEC_VENCIMIENTO; 
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET V_REQUERIDAS = 0;
  
START TRANSACTION;

 SET V_REQUERIDAS = V_CAN_MEDICAMENTO;

  OPEN LOTE_CUR;

  bucle: LOOP

    FETCH LOTE_CUR INTO V_LOTE,V_DISPONIBLE;

    IF V_REQUERIDAS = 0 THEN
      LEAVE bucle;
    END IF;
   
      IF V_DISPONIBLE >= V_REQUERIDAS THEN
      UPDATE TBL_PR_DETALLE_LOTE SET
      UNI_DISPONIBLES = UNI_DISPONIBLES - V_REQUERIDAS
      WHERE COD_MEDICAMENTO = V_COD_MEDICAMENTO and COD_COMPRA_MEDICAMENTO = V_LOTE ;
    
      UPDATE TBL_PR_MEDICAMENTO SET
      CAN_DISPONIBLE = CAN_DISPONIBLE - V_REQUERIDAS
      WHERE COD_MEDICAMENTO = V_COD_MEDICAMENTO;
      
      SET V_REQUERIDAS = 0;
      
      ELSEIF V_DISPONIBLE > 0  AND  V_DISPONIBLE  < V_REQUERIDAS THEN
       UPDATE TBL_PR_DETALLE_LOTE SET
      UNI_DISPONIBLES = UNI_DISPONIBLES - UNI_DISPONIBLES
      WHERE COD_MEDICAMENTO = V_COD_MEDICAMENTO and COD_COMPRA_MEDICAMENTO = V_LOTE;
      
       
       
      UPDATE TBL_PR_MEDICAMENTO SET
      CAN_DISPONIBLE = CAN_DISPONIBLE - V_DISPONIBLE
      WHERE COD_MEDICAMENTO = V_COD_MEDICAMENTO;
      
      SET V_REQUERIDAS = V_REQUERIDAS - V_DISPONIBLE;
      END IF;
    
    

  END LOOP bucle;
    INSERT INTO TBL_MI_ORDENT(
    COD_MEDICAMENTO,
    CAN_MEDICAMENTO,
    FEC_REGISTRO
  )
  VALUES(
    V_COD_MEDICAMENTO,
    V_CAN_MEDICAMENTO,
    CURDATE()
  );
  CLOSE LOTE_CUR;

  COMMIT;
END ;;
