CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_COMPRA_GANADO`( IN V_NOM_GANADO VARCHAR(50), 
IN V_CLR_GANADO VARCHAR(50), 
IN V_COD_ESTADO BIGINT, 
IN V_COD_LUGAR BIGINT,
IN V_PES_ACTUAL DECIMAL(10,2), 
IN V_FIE_GANADO CHAR(4), 
IN V_RAZ_GANADO VARCHAR(50), 
IN V_SEX_GANADO ENUM('MACHO','HEMBRA'), 
IN V_PRE_GANADO DECIMAL(10,2))
BEGIN 
START TRANSACTION;
SELECT @V_COD_COMPRA_GANADO :=MAX(COD_COMPRA_GANADO) FROM TBL_MC_COMPRA_GANADO where STATUS_LOTE ='EN PROCESO';

INSERT INTO TBL_MC_DETALLE_COMPRAG(
NOM_GANADO,
CLR_GANADO,
COD_ESTADO,
COD_LUGAR,
PES_ACTUAL,
FIE_GANADO,
COD_RAZA,         
SEX_GANADO,
PRE_GANADO,
COD_COMPRA_GANADO,
STATUS_COMPRA
)VALUES(
V_NOM_GANADO,
V_CLR_GANADO,
V_COD_ESTADO,
V_COD_LUGAR,
V_PES_ACTUAL,
V_FIE_GANADO,
 V_RAZ_GANADO,
V_SEX_GANADO,
V_PRE_GANADO,
@V_COD_COMPRA_GANADO,
DEFAULT 
);
UPDATE tbl_mc_compra_ganado SET
  CAN_TOTAL = CAN_TOTAL +1,
  TOTAL_PRECIO = TOTAL_PRECIO + V_PRE_GANADO
WHERE COD_COMPRA_GANADO = @V_COD_COMPRA_GANADO;





COMMIT;
END ;;
