CREATE DEFINER=`root`@`localhost` PROCEDURE `UPDATE_COMPRA_MEDICAMENTO`(IN V_COD_COMPRA_MEDICAMENTO BIGINT, IN V_COD_MEDICAMENTO BIGINT, IN V_CAN_MEDICAMENTO INT, IN V_PRE_UNITARIO DECIMAL(10,2), IN V_FEC_VENCIMIENTO DATE, IN V_FEC_COMPRA DATE, IN V_COD_PERSONA BIGINT)
BEGIN
  START TRANSACTION;
  
  SELECT @CAN_ANTERIOR:=CAN_MEDICAMENTO FROM TBL_PR_COMPRA_MEDICAMENTO 
  WHERE COD_COMPRA_MEDICAMENTO = V_COD_COMPRA_MEDICAMENTO;
  
  
  UPDATE TBL_PR_COMPRA_MEDICAMENTO
  SET CAN_MEDICAMENTO = V_CAN_MEDICAMENTO,  
      PRE_UNITARIO = V_PRE_UNITARIO,       
      FEC_VENCIMIENTO = V_FEC_VENCIMIENTO, 
