      COD_PERSONA = COD_PERSONA
  WHERE COD_COMPRA_MEDICAMENTO = V_COD_COMPRA_MEDICAMENTO;
  
  
  UPDATE TBL_PR_MEDICAMENTO
  SET CAN_DISPONIBLE = @CAN_ANTERIOR - CAN_DISPONIBLE + V_CAN_MEDICAMENTO
  WHERE COD_MEDICAMENTO = V_COD_MEDICAMENTO;
  
  COMMIT;
  
END ;;
